import { useState } from "react";

import "./Item.css";
import CalendarDate from "./CalendarDate";
import Card from "./Card";

const Item = (props) => {
  const [title, setTitle] = useState(props.title);
  const [value, setValue] = useState("Текст");
  const [likesCount, incrementLikes] = useState(0);

  const clickHandler = () => {
    setTitle("Updated");
    console.log(title);
  };
  const inputHandler = (event) => {
    setValue(event.target.value);
  };
  const incrementHandler = () => {
    incrementLikes(likesCount + 1);
  };

  return (
    <Card className="item">
      <CalendarDate date={props.date} />
      <div className="item__description">
        <h2>{title}</h2>
        <div className="item__price">${props.cost}</div>
        {/* <p>{value}</p> */}
        {/* <p>{likesCount}</p> */}
        {/* <input type="text" value={value} onChange={inputHandler}></input>*/}
      </div>
      {/* <button onClick={clickHandler}>Change Title</button>*/}
      {/* <button onClick={incrementHandler}>Increment</button> */}
    </Card>
  );
};

export default Item;
