import ItemsList from "./components/ItemsList";
import NewItem from "./components/NewItems/NewItem";

import { useState } from "react";
const TEST_DATA = [
  {
    id: 1,
    title: "Plane Tickets",
    cost: 450,
    date: new Date("01.12.2023"),
  },
  {
    id: 2,
    title: "MacDonalds",
    cost: 5,
    date: new Date("26.01.2023"),
  },
  {
    id: 3,
    title: "Headphones",
    cost: 80,
    date: new Date(2023, 11, 1),
  },
  {
    id: 4,
    title: "Chocolate Bar",
    cost: 10,
    date: new Date("01.12.2023"),
  },
];

const App = () => {
  const [items, setItems] = useState(TEST_DATA);
  let options = {
    year: "numeric",
    month: "long",
    day: "numeric",
    locale: "ru-RU",
  };
  const addItemHandler = (item) => {
    item.date = item.date.toLocaleDateString("ru-RU", options);

    console.log(item);
    setItems((prevItems) => {
      return [item, ...prevItems];
    });
  };

  return (
    <div>
      <NewItem onAddItem={addItemHandler} />
      <ItemsList items={items} />
    </div>
  );
};

export default App;
