import Chart from "./Chart/Chart";

const ItemsChart = (props) => {
  const chartDataPoints = [
    { label: "Jan", value: 0 },
    { label: "Feb", value: 0 },
    { label: "Mar", value: 0 },
    { label: "Apr", value: 0 },
    { label: "May", value: 0 },
    { label: "Jun", value: 0 },
    { label: "Jul", value: 0 },
    { label: "Aug", value: 0 },
    { label: "Sep", value: 0 },
    { label: "Oct", value: 0 },
    { label: "Nov", value: 0 },
    { label: "Dec", value: 0 },
  ];
  for (const item in props.items) {
    const itemMonth = item.date.getMonth();
    chartDataPoints[itemMonth].value += item.cost;
  }
  return <Chart dataPoints={chartDataPoints} />;
};

export default ItemsChart;
