import { useState } from "react";
import "./ItemsFilter.css";

const ItemsFilter = () => {
  const [selectedYear, setYear] = useState("2023");
  const changeYearHandler = (event) => {
    setYear(event.targer.value);
  };
  return (
    <div className="items-filter">
      <div className="items-filter__control">
        <label>Filter by year</label>
        <select onChange={changeYearHandler}>
          <option value={selectedYear}>2019</option>
          <option value={selectedYear}>2022</option>
          <option value={selectedYear}>2021</option>
          <option value={selectedYear}>2020</option>
        </select>
      </div>
    </div>
  );
};

export default ItemsFilter;
